import { MergeType } from '../consts/ErrorLogConsts.js';
import { HvigorErrorInfo } from '../logger/HvigorErrorInfo.js';
/**
 * 合并规则定义。mergeKey指定的字段不应该出现在合并规则里。
 */
export interface MergeOption {
    /**
     * 当按其他字段重复进行合并时，指定不同ErrorInfo中各code字段的合并方式。
     */
    code?: MergeType;
    /**
     * 当按其他字段重复进行合并时，指定不同ErrorInfo中各cause字段的合并方式。
     */
    cause?: MergeType;
    /**
     * 当按其他字段重复进行合并时，指定不同ErrorInfo中各cause字段的合并方式。
     */
    position?: MergeType;
    /**
     * 当按其他字段重复进行合并时，指定不同ErrorInfo中各solutions字段的合并方式。
     */
    solutions?: MergeType;
    /**
     * 当按其他字段重复进行合并时，指定不同ErrorInfo中各moreInfo字段的合并方式。
     */
    moreInfo?: MergeType;
    /**
     * 自定义函数用于合并错误信息时比较两个元素的错误码是否相同。默认为字符串比较。
     * @param a
     * @param b
     * @returns
     */
    compareCode?: (a: HvigorErrorInfo, b: HvigorErrorInfo) => boolean;
    /**
     * 自定义函数用于合并错误信息时比较两个元素的错误原因是否相同。默认为字符串比较。
     * @param a
     * @param b
     * @returns
     */
    compareCause?: (a: HvigorErrorInfo, b: HvigorErrorInfo) => boolean;
    /**
     * 自定义函数用于合并错误信息时比较两个元素的报错位置是否相同。默认为字符串比较。
     * @param a
     * @param b
     * @returns
     */
    comparePosition?: (a: HvigorErrorInfo, b: HvigorErrorInfo) => boolean;
    /**
     * 自定义函数用于合并错误信息时比较两个元素的解决方案是否相同。默认为字符串比较。
     * @param a
     * @param b
     * @returns
     */
    compareSolutions?: (a: HvigorErrorInfo, b: HvigorErrorInfo) => boolean;
    /**
     * 自定义函数用于合并错误信息时比较两个元素的帮助信息是否相同。默认为字符串比较。
     * @param a
     * @param b
     * @returns
     */
    compareMoreInfo?: (a: HvigorErrorInfo, b: HvigorErrorInfo) => boolean;
}
