import { HvigorErrorInfo } from '../logger/HvigorErrorInfo.js';
import { MoreInfo } from '../types/ErrorLogTypes.js';
import { MergeOption } from './MergeOption.js';
/**
 * 定义合并两个HvigorErrorInfo的两个字段的接口
 */
export interface MergeTwoProperty {
    /**
     * 合并两个错误对象的字段
     * @param firstError 前一个要合并的错误对象
     * @param secondError 后一个要合并的错误对象
     * @param option 合并规则：定义字段的合并类型，默认为：COLLECT_LAST
     * COLLECT_LAST = 1：收集所有ErrorInfo里的相关字段，按入栈的先后顺序仅保留最后一个ErrorInfo的数据。
     * COLLECT_FIRST = 2： 收集所有ErrorInfo里的相关字段，按入栈的先后顺序仅保留第一个ErrorInfo的数据。
     * COLLECT_ALL = 3： 收集所有ErrorInfo里的相关字段，按入栈的先后顺序拼接合并。
     */
    mergeTwoKey(firstError: HvigorErrorInfo, secondError: HvigorErrorInfo, option: MergeOption): string | string[] | MoreInfo | undefined;
}
